package durithon.duri;import android.content.Intent;import android.os.Bundle;import android.os.Handler;import android.os.Looper;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.Button;import com.github.lzyzsd.circleprogress.DonutProgress;public class HomeActivity extends AppCompatActivity {    Button walk_btn;    Button record_btn;    Button music_btn;    DonutProgress donutProgress;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_home);        donutProgress = (DonutProgress) findViewById(R.id.donut_progress);        walk_btn = (Button) findViewById(R.id.inner2);        walk_btn.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                startActivity(new Intent(HomeActivity.this, MapActivity.class));            }        });        record_btn = (Button) findViewById(R.id.inner3);        record_btn.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                startActivity(new Intent(HomeActivity.this, RecordActivity.class));            }        });        music_btn = (Button) findViewById(R.id.inner);        music_btn.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                startActivity(new Intent(HomeActivity.this, MusicActivity.class));            }        });        Donutprogresszzzz donutProgress = new Donutprogresszzzz();        donutProgress.start();    }    class Donutprogresszzzz extends Thread{        int count = 0;        @Override        public void run() {            super.run();            while(count <= 75){                try {                    Thread.sleep(15);                } catch (InterruptedException e) {                    e.printStackTrace();                }                new Handler(Looper.getMainLooper()).post(new Runnable() {                    @Override                    public void run() {                        count += 1;                        donutProgress.setDonut_progress(String.valueOf(count));                    }                });            }        }    }}