package durithon.duri;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.AdapterView;import android.widget.BaseAdapter;import android.widget.ListView;import java.util.ArrayList;public class MapHistoryListActivity extends AppCompatActivity {    ListView map_history_list;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_map_history_list);        ArrayList<String> map_history_data_list = new ArrayList<>();        for(int i=0; i<20; i++){            map_history_data_list.add(String.valueOf(i));        }        map_history_list = (ListView) findViewById(R.id.map_history_list);        HistoryAdapter history_adapter = new HistoryAdapter(map_history_data_list, MapHistoryListActivity.this);        map_history_list.setAdapter(history_adapter);        map_history_list.setOnItemClickListener(new AdapterView.OnItemClickListener()        {            public void onItemClick(AdapterView<?> parentView, View clickedView, int position, long id) {                startActivity(new Intent(MapHistoryListActivity.this, MapDetailHistoryActivity.class));            }        });        map_history_list.setDividerHeight(0);    }    class HistoryAdapter extends BaseAdapter {        ArrayList<String> data_list;        Context con;        LayoutInflater inflater;        HistoryAdapter(ArrayList<String> data_list, Context con){            this.data_list=data_list;            this.con=con;            inflater= (LayoutInflater) this.con.getSystemService(Context.LAYOUT_INFLATER_SERVICE);        }        @Override        public int getCount() {            return data_list.size();        }        @Override        public String getItem(int i) {            return data_list.get(i);        }        @Override        public long getItemId(int i) {            return i;        }        @Override        public View getView(int i, View convert_view, ViewGroup viewGroup) {            convert_view = inflater.inflate(R.layout.activity_map_history_list_item,viewGroup, false);            return convert_view;        }    }}