package durithon.duri;import android.content.Context;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ListView;import android.widget.TextView;import java.util.ArrayList;public class RecordActivity extends AppCompatActivity {    ListView record_list;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_record);        ArrayList<Data> record_data_list = new ArrayList<>();        record_data_list.add(new Data("앉아"));        record_data_list.add(new Data("돌아와"));        record_data_list.add(new Data("여기로와"));        record_data_list.add(new Data("밥먹자"));        record_data_list.add(new Data("총"));        record_data_list.add(new Data("엎드려"));        this.record_list = (ListView) findViewById(R.id.lst_record);        RecordAdapter history_adapter = new RecordAdapter(record_data_list, RecordActivity.this);        this.record_list.setAdapter(history_adapter);        this.record_list.setDividerHeight(0);    }    class RecordAdapter extends BaseAdapter {        ArrayList<Data> data_list;        Context con;        LayoutInflater inflater;        RecordAdapter(ArrayList<Data> data_list, Context con){            this.data_list=data_list;            this.con=con;            inflater= (LayoutInflater) this.con.getSystemService(Context.LAYOUT_INFLATER_SERVICE);        }        @Override        public int getCount() {            return data_list.size();        }        @Override        public String getItem(int i) {            return data_list.get(i).title;        }        @Override        public long getItemId(int i) {            return i;        }        @Override        public View getView(int i, View convert_view, ViewGroup viewGroup) {            convert_view = inflater.inflate(R.layout.activity_map_detail_history_list_item,viewGroup, false);            TextView view = (TextView) convert_view.findViewById(R.id.textView7);            view.setText(data_list.get(i).title);            return convert_view;        }    }}